  array('id' => '279','item_code' => '32/ 6','item_name' => 'مواقفنا من أولادنا: إمتلاك أو إطلاق','author_id' => '819','item_size' => '61','type_id' => '7','cat_id' => '21','item_copies' => '1','book_details' => 'مواقفنا من أولادنا: امتلاك أو إطلاق

